import { Base, Incident } from '../';
import { IncidentPost, IncidentDelete } from '../utils/Typings';
import { AxiosResponse } from 'axios';
/**
 * A manager of the incidents belonging to a status page
 *
 * @export
 * @class IncidentsManager
 * @extends {Base}
 */
export default class IncidentsManager extends Base {
    /**
     * Gets all incidents
     *
     * @return {*}  {Promise<Incident[]>}
     * @memberof IncidentsManager
     */
    getAll(): Promise<Incident[]>;
    /**
     * Gets one incident
     *
     * @param {string} incidentID The incident ID
     * @return {*}  {Promise<Incident>}
     * @memberof IncidentsManager
     */
    get(incidentID: string): Promise<Incident>;
    /**
     * Creates an incident
     *
     * @param {IncidentPost} data The data to create the incident
     * @return {*}  {Promise<Incident>}
     * @memberof IncidentsManager
     */
    add(data: IncidentPost): Promise<Incident>;
    /**
     * Edits an incident
     *
     * @param {string} incidentID The incident ID
     * @param {IncidentPost} data The data to edit the incident
     * @return {*}  {Promise<Incident>}
     * @memberof IncidentsManager
     */
    update(incidentID: string, data: IncidentPost): Promise<Incident>;
    /**
     * Deletes an incident
     *
     * @param {string} incidentID The incident ID
     * @return {*}  {Promise<AxiosResponse<IncidentDelete>>}
     * @memberof IncidentsManager
     */
    delete(incidentID: string): Promise<AxiosResponse<IncidentDelete>>;
    private _check;
}
